glsl4.vert:
precision highp float;
layout(set = 0, binding = 0) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
};
layout(set = 0, binding = 1) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
};
#if USE_LOCAL
layout(set = 2, binding = 0) uniform CCLocal {
  highp mat4 cc_matWorld;
  highp mat4 cc_matWorldIT;
  highp vec4 cc_lightingMapUVParam;
};
#endif
layout(location = 0) in vec3 a_position;
layout(location = 1) in vec2 a_texCoord;
layout(location = 2) in vec4 a_color;
layout(location = 3) in vec4 a_color2;
layout(location = 0) out vec4 v_light;
layout(location = 1) out vec4 v_dark;
layout(location = 2) out vec2 uv0;
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  #if USE_LOCAL
    pos = cc_matWorld * pos;
  #endif
  #if USE_PIXEL_ALIGNMENT
    pos = cc_matView * pos;
    pos.xyz = floor(pos.xyz);
    pos = cc_matProj * pos;
  #else
    pos = cc_matViewProj * pos;
  #endif
  uv0 = a_texCoord;
  v_light = a_color;
  v_dark = a_color2;
  return pos;
}
void main() { gl_Position = vert(); }

glsl4.frag:
precision highp float;
vec4 CCSampleTexture(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
    return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
    return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(set = 1, binding = 0) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}
layout(location = 0) in vec4 v_light;
layout(location = 1) in vec4 v_dark;
#if USE_TEXTURE
  layout(location = 2) in vec2 uv0;
  layout(set = 2, binding = 10) uniform sampler2D cc_spriteTexture;
#endif
vec4 frag () {
  vec4 texColor = vec4(1, 1, 1, 1);
  vec4 o;
  #if USE_TEXTURE
    texColor *= CCSampleTexture(cc_spriteTexture, uv0);
    #if IS_GRAY
      float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
      o.r = o.g = o.b = gray;
    #endif
  #endif
   o.a = texColor.a * v_light.a;
  o.rgb = ((texColor.a - 1.0) * v_dark.a + 1.0 - texColor.rgb) * v_dark.rgb + texColor.rgb * v_light.rgb;
  ALPHA_TEST(o);
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

